--
-- Table structure for table `cmf_ask`
--

DROP TABLE IF EXISTS `cmf_ask`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `cmf_ask` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `listid` int(11) NOT NULL DEFAULT '0' COMMENT '问卷id',
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '问题标题',
  `list_order` varchar(255) NOT NULL DEFAULT '9999' COMMENT '排序',
  `count_option` int(11) NOT NULL DEFAULT '0' COMMENT '选项数量',
  `count_max` int(255) NOT NULL DEFAULT '0' COMMENT '最大选择数量',
  `count_min` int(11) NOT NULL DEFAULT '0' COMMENT '最小选择数量',
  `option_json` longtext COMMENT '选项（json）',
  `thumbs_json` longtext COMMENT '选项图',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=56 DEFAULT CHARSET=utf8 COMMENT='问题表';
/*!40101 SET character_set_client = @saved_cs_client */;

