DROP TABLE IF EXISTS `cmf_knowledge`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `cmf_knowledge` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '标题',
  `content` longtext NOT NULL COMMENT '文章内容（H5）',
  `addtime` bigint(20) NOT NULL DEFAULT '0' COMMENT '发布时间',
  `list_order` int(11) NOT NULL DEFAULT '9999' COMMENT '排序',
  `view` int(11) NOT NULL DEFAULT '0' COMMENT '阅读次数',
  `hid` int(11) DEFAULT '0' COMMENT '党史历史id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=40 DEFAULT CHARSET=utf8 COMMENT='党史文章表';
/*!40101 SET character_set_client = @saved_cs_client */;

