DROP TABLE IF EXISTS `cmf_scores_record`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `cmf_scores_record` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',
  `change_score` int(11) NOT NULL DEFAULT '0' COMMENT '积分变化值',
  `reason` tinyint(1) NOT NULL DEFAULT '0' COMMENT '触发原因(每天第一次) 1打开小程序、2阅读文章、3留言后、4答题后 、5商城兑换',
  `addtime` int(11) NOT NULL DEFAULT '0' COMMENT '添加时间',
  `type` tinyint(1) DEFAULT '0' COMMENT '1增加 2减少 默认为0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=467 DEFAULT CHARSET=utf8 COMMENT='用户积分记录表';
/*!40101 SET character_set_client = @saved_cs_client */;

