-- Table structure for table `cmf_tests_user`
--

DROP TABLE IF EXISTS `cmf_tests_user`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `cmf_tests_user` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `uid` bigint(20) NOT NULL DEFAULT '0' COMMENT '用户ID',
  `testsid` bigint(20) NOT NULL DEFAULT '0' COMMENT '试题ID',
  `answer` text COMMENT '作答信息JSON',
