DROP TABLE IF EXISTS `cmf_zan_collect`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `cmf_zan_collect` (
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',
  `newsid` int(11) NOT NULL DEFAULT '0' COMMENT '新闻id',
  `type` tinyint(255) NOT NULL DEFAULT '0' COMMENT '0新闻 1思想交流',
  `zan` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否赞',
  `collect` tinyint(255) NOT NULL DEFAULT '0' COMMENT '是否收藏',
  `collecttime` bigint(20) NOT NULL DEFAULT '0' COMMENT '收藏时间',
  `zantime` bigint(20) NOT NULL DEFAULT '0' COMMENT '点赞时间',
  PRIMARY KEY (`uid`,`newsid`,`type`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='文章收藏和赞的统计';
